(4, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-03-27 01:29:49', NULL, 0, '', NULL, 0, 1),
(5, 'wc-admin-orders-milestone', 'info', 'en_US', 'First order', 'Congratulations on getting your first order from a customer! Learn how to manage your orders.', 'trophy', '{}', 'unactioned', 'woocommerce-admin', '2020-03-27 01:29:49', NULL, 0, '', NULL, 0, 1),
(6, 'wc-update-db-reminder', 'update', 'en_US', 'đã update cơ sở dữ liệu', 'Dữ liệu WooCommerce đã được cập nhật thành công. Cảm ơn bạn đã sử dụng phiên bản mới nhất!', 'info', '{}', 'actioned', 'woocommerce-core', '2020-03-25 21:33:03', NULL, 0, 'plain', NULL, 0, 1),
(7, 'wayflyer_q3_2021', 'marketing', 'en_US', 'Grow your revenue with Wayflyer financing and analytics', 'Flexible financing tailored to your needs by <a href=\"https://woocommerce.com/products/wayflyer/\">Wayflyer</a> – one fee, no interest rates, penalties, equity, or personal guarantees. Based on your store\'s performance, Wayflyer can provide the financing you need to grow and the analytical insights to help you spend it.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(8, 'eu_vat_changes_2021', 'marketing', 'en_US', 'Get your business ready for the new EU tax regulations', 'On July 1, 2021, new taxation rules will come into play when the <a href=\"https://ec.europa.eu/taxation_customs/business/vat/modernising-vat-cross-border-ecommerce_en\">European Union (EU) Value-Added Tax (VAT) eCommerce package</a> takes effect.<br /><br />The new regulations will impact virtually every B2C business involved in cross-border eCommerce trade with the EU.<br /><br />We therefore recommend <a href=\"https://woocommerce.com/posts/new-eu-vat-regulations\">familiarizing yourself with the new updates</a>, and consult with a tax professional to ensure your business is following regulations and best practice.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(9, 'paypal_ppcp_gtm_2021', 'marketing', 'en_US', 'Offer more options with the new PayPal', 'Get the latest PayPal extension for a full suite of payment methods with extensive currency and country coverage.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(10, 'facebook_pixel_api_2021', 'marketing', 'en_US', 'Improve the performance of your Facebook ads', 'Enable Facebook Pixel and Conversions API through the latest version of Facebook for WooCommerce for improved measurement and ad targeting capabilities.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(11, 'facebook_ec_2021', 'marketing', 'en_US', 'Sync your product catalog with Facebook to help boost sales', 'A single click adds all products to your Facebook Business Page shop. Product changes are automatically synced, with the flexibility to control which products are listed.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(12, 'ecomm-need-help-setting-up-your-store', 'info', 'en_US', 'Need help setting up your Store?', 'Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. We’re happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(13, 'woocommerce-services', 'info', 'en_US', 'WooCommerce Shipping & Tax', 'WooCommerce Shipping &amp; Tax helps get your store \"ready to sell\" as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(14, 'ecomm-unique-shopping-experience', 'info', 'en_US', 'For a shopping experience as unique as your customers', 'Product Add-Ons allow your customers to personalize products while they’re shopping on your online store. No more follow-up email requests—customers get what they want, before they’re done checking out. Learn more about this extension that comes included in your plan.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(15, 'wc-admin-getting-started-in-ecommerce', 'info', 'en_US', 'Getting Started in eCommerce - webinar', 'We want to make eCommerce and this process of getting started as easy as possible for you. Watch this webinar to get tips on how to have our store up and running in a breeze.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(16, 'your-first-product', 'info', 'en_US', 'Your first product', 'That’s huge! You’re well on your way to building a successful online store — now it’s time to think about how you’ll fulfill your orders.<br /><br />Read our shipping guide to learn best practices and options for putting together your shipping strategy. And for WooCommerce stores in the United States, you can print discounted shipping labels via USPS with <a href=\"https://href.li/?https://woocommerce.com/shipping\" target=\"_blank\">WooCommerce Shipping</a>.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(17, 'wc-square-apple-pay-boost-sales', 'marketing', 'en_US', 'Boost sales with Apple Pay', 'Now that you accept Apple Pay® with Square you can increase conversion rates by letting your customers know that Apple Pay® is available. Here’s a marketing guide to help you get started.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(18, 'wc-square-apple-pay-grow-your-business', 'marketing', 'en_US', 'Grow your business with Square and Apple Pay ', 'Now more than ever, shoppers want a fast, simple, and secure online checkout experience. Increase conversion rates by letting your customers know that you now accept Apple Pay®.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(19, 'wcpay-apple-pay-is-now-available', 'marketing', 'en_US', 'Apple Pay is now available with WooCommerce Payments!', 'Increase your conversion rate by offering a fast and secure checkout with <a href=\"https://woocommerce.com/apple-pay/?utm_source=inbox&amp;utm_medium=product&amp;utm_campaign=wcpay_applepay\" target=\"_blank\">Apple Pay</a>®. It’s free to get started with <a href=\"https://woocommerce.com/payments/?utm_source=inbox&amp;utm_medium=product&amp;utm_campaign=wcpay_applepay\" target=\"_blank\">WooCommerce Payments</a>.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(20, 'wcpay-apple-pay-boost-sales', 'marketing', 'en_US', 'Boost sales with Apple Pay', 'Now that you accept Apple Pay® with WooCommerce Payments you can increase conversion rates by letting your customers know that Apple Pay® is available. Here’s a marketing guide to help you get started.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(21, 'wcpay-apple-pay-grow-your-business', 'marketing', 'en_US', 'Grow your business with WooCommerce Payments and Apple Pay', 'Now more than ever, shoppers want a fast, simple, and secure online checkout experience. Increase conversion rates by letting your customers know that you now accept Apple Pay®.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(22, 'wc-admin-optimizing-the-checkout-flow', 'info', 'en_US', 'Optimizing the checkout flow', 'It’s crucial to get your store’s checkout as smooth as possible to avoid losing sales. Let’s take a look at how you can optimize the checkout experience for your shoppers.', 'info', '{}', 'unactioned', 'woocommerce.com', '2023-01-17 04:06:47', NULL, 0, 'plain', '', 0, 1),
(23, 'wc-admin-first-five-things-to-customize', 'info', 'en_US', 'The first 5 things to customize in your store', 'Deciding what to start with first is tricky. To help you properly prioritize, we\'ve put together this short list of the first few things you should customize in WooCommerce.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(24, 'wc-payments-qualitative-feedback', 'info', 'en_US', 'WooCommerce Payments setup - let us know what you think', 'Congrats on enabling WooCommerce Payments for your store. Please share your feedback in this 2 minute survey to help us improve the setup process.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(25, 'share-your-feedback-on-paypal', 'info', 'en_US', 'Share your feedback on PayPal', 'Share your feedback in this 2 minute survey about how we can make the process of accepting payments more useful for your store.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(26, 'wcpay_instant_deposits_gtm_2021', 'marketing', 'en_US', 'Get paid within minutes – Instant Deposits for WooCommerce Payments', 'Stay flexible with immediate access to your funds when you need them – including nights, weekends, and holidays. With <a href=\"https://woocommerce.com/products/woocommerce-payments/?utm_source=inbox&amp;utm_medium=product&amp;utm_campaign=wcpay_instant_deposits\">WooCommerce Payments\'</a> new Instant Deposits feature, you’re able to transfer your earnings to a debit card within minutes.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(27, 'google_listings_and_ads_install', 'marketing', 'en_US', 'Drive traffic and sales with Google', 'Reach online shoppers to drive traffic and sales for your store by showcasing products across Google, for free or with ads.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(28, 'wc-subscriptions-security-update-3-0-15', 'info', 'en_US', 'WooCommerce Subscriptions security update!', 'We recently released an important security update to WooCommerce Subscriptions. To ensure your site’s data is protected, please upgrade <strong>WooCommerce Subscriptions to version 3.0.15</strong> or later.<br /><br />Click the button below to view and update to the latest Subscriptions version, or log in to <a href=\"https://woocommerce.com/my-dashboard\">WooCommerce.com Dashboard</a> and navigate to your <strong>Downloads</strong> page.<br /><br />We recommend always using the latest version of WooCommerce Subscriptions, and other software running on your site, to ensure maximum security.<br /><br />If you have any questions we are here to help — just <a href=\"https://woocommerce.com/my-account/create-a-ticket/\">open a ticket</a>.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
(29, 'woocommerce-core-update-5-4-0', 'info', 'en_US', 'Update to WooCommerce 5.4.1 now', 'WooCommerce 5.4.1 addresses a checkout issue discovered in WooCommerce 5.4. We recommend upgrading to WooCommerce 5.4.1 as soon as possible.', 'info', '{}', 'pending', 'woocommerce.com', '2021-09-19 03:54:21', NULL, 0, 'plain', '', 0, 1),
